import FormLabel from '@material-ui/core/FormLabel';

const useStyles = makeStyles(() => ({
  root: {
    display: 'flex',
    width: '600px',
    height: '200px',
    marginLeft: '100px',
    marginTop: '20px',
    background: 'linear-gradient(329deg, rgb(19 15 62) 0%, rgba(28,56,164,1) 100%)',
    color: '#fff',
    borderRadius: '20px',
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 151,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },

  button: {
    marginLeft: '10px',
    marginBottom: '10px',
    background: 'linear-gradient(329deg, rgba(24,77,208,1) 0%, rgba(34,208,215,1) 100%)',
    color: '#fefefe',
    transition: '0.5s ease',
    '&:hover': {
      background: 'linear-gradient(129deg, rgba(24,77,208,1) 0%, rgba(34,208,215,1) 100%)',
      color: '#FFF',
  },
  color:'#00000'
  },

  catogery: {
    marginLeft: '230px'
  }


}));

export default function TaskCard() {
  const classes = useStyles();
  const theme = useTheme();

  const [value, setValue] = React.useState('All');

  const handleChange = (event) => {

    setValue(event.target.value);
    let result = taskDetails
     result = result.filter(function(obj, index){
      return obj.catogery===event.target.value;
  });
  if (event.target.value == "All") {
    result = taskDetails
  }

  render (
    <div>
    {result.map((tasks, index) => (

      <Card className={classes.root} color="primary"  key={index}>
        <div className={classes.details}>
          <CardContent className={classes.content}>
            <Typography component="h5" variant="h5">
              {tasks.taskName}
              <span className={classes.catogery}>
              <Tooltip title={tasks.catogery}>
                <IconButton aria-label={tasks.catogery} color='secondary'>
           {(() => {
          switch (tasks.catogery) {
            case "Code":   return <CodeIcon />;
            case "Design": return <GestureIcon />;
            case "Explore":  return <FindInPageIcon />;
          }
        })()}
            </IconButton>
            </Tooltip>
            </span>
            </Typography>
            <Typography variant="subtitle1"  color={theme.text.secondary.main} className={classes.cat} >
              {tasks.shortDescription}
            </Typography>
          </CardContent>

          <div className={classes.controls}>
          <Button variant="contained" className={classes.button}>
            View
          </Button>

          </div>
        </div>
      </Card>
      ))}
      </div>
  )
  console.log(taskDetails)


