import React, { Component, useState }  from 'react';
import { unmountComponentAtNode, render } from "react-dom";
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { Avatar, IconButton } from '@material-ui/core';
import Tooltip from '@material-ui/core/Tooltip';
import CodeIcon from '@material-ui/icons/Code';
import BrushIcon from '@material-ui/icons/Brush';
import FindInPageIcon from '@material-ui/icons/FindInPage';
import taskDetails from '../../Data/tasks.json'
import theme from '../../theme'
import { Link } from 'react-router-dom';
import { render as renderelement } from '@testing-library/react';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import PropTypes from 'prop-types';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Fab from '@material-ui/core/Fab';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Zoom from '@material-ui/core/Zoom';

const useStyles = makeStyles(() => ({
  root: {
    display: 'flex',
    width: '600px',
    height: '230px',
    marginLeft: '100px',
    marginTop: '20px',
    backgroundColor: '#183d5d',
    color: '#fff',
    borderRadius: '15px',
    ['@media (max-width:540px)']: {
      position: 'relative',
      width: '95%',
      margin: 'auto',
      marginTop: '20px',
    },
    ['@media (max-width:350px)']: {

      width: '90%',
      margin: 'auto',
      marginTop: '20px',
    }
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
    padding: '10px'
  },
  content: {
    flex: '1 0 auto',

  },
  cover: {
    width: 151,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },

  button: {
    marginLeft: '10px',
    marginBottom: '10px',
    backgroundColor: '#3ed1b8',
    color: '#000',
    transition: '0.5s ease',
    '&:hover': {
      backgroundColor: '#2d9886',
      color: '#fff',
  },

  },

  catogery: {
    float: 'right',
    marginTop: '-10px'
  },
  filter: {
    backgroundColor: '#183d5d',
    float: 'right',
    marginRight: '20%',
    padding: '50px',
    borderRadius: '15px',
    width: '15%',
    ['@media (max-width:540px)']: {
      position: 'relative',
      width: '65%',
      margin: 'auto',
      marginBottom: '20px',
      float: 'none',

    }
  },

  filterOptions: {
    color: '#fff',
    ['@media (max-width:540px)']: {
      flexDirection: 'row',
    }
  }


}));

function ScrollTop(props) {
  const { children, window } = props;
  const classes = useStyles();
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({
    target: window ? window() : undefined,
    disableHysteresis: true,
    threshold: 100,
  });

  const handleClick = (event) => {
    const anchor = (event.target.ownerDocument || document).querySelector('#back-to-top-anchor');

    if (anchor) {
      anchor.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  };

  return (
    <Zoom in={trigger}>
      <div onClick={handleClick} role="presentation" className={classes.root}>
        {children}
      </div>
    </Zoom>
  );
}

ScrollTop.propTypes = {
  children: PropTypes.element.isRequired,
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default function TaskCard(props) {
  const classes = useStyles();
  const theme = useTheme();
  const [value, setValue] = React.useState('All');
  const [data, setData] = useState(taskDetails)

  const handleChange = (event) => {

    setValue(event.target.value);
     let result = taskDetails
     setData(result.filter(function(obj, index){
      return obj.catogery===event.target.value;})) 
  ;
  if (event.target.value == "All") {
    setData(taskDetails) 
  }
}

  return (
    <div className='eee'>
      <div className={classes.filter}>
      <FormControl component="fieldset">
      <FormLabel component="legend">Filter Tasks</FormLabel>
      <RadioGroup className={classes.filterOptions} aria-label="tasks" name="filter" value={value} onChange={handleChange}>
        <FormControlLabel value="All" control={<Radio color='secondary' />} label="All Tasks" />
        <FormControlLabel value="Code" control={<Radio color='secondary'/>} label="Coding Tasks" />
        <FormControlLabel value="Design" control={<Radio color='secondary'/>} label="Design Tasks" />
        <FormControlLabel value="Explore"  control={<Radio color='secondary'/>} label="Explore Tasks" />
      </RadioGroup>
    </FormControl>
    </div>
      {data.map((tasks, index) => (

    <Card className={classes.root} color="primary"  key={index} >
      <div className={classes.details}>
        <CardContent className={classes.content}>
          <Typography component="h5" variant="h5">
            {tasks.taskName}
            <span className={classes.catogery}>
            <Tooltip title={tasks.catogery}>
              <IconButton aria-label={tasks.catogery} color='secondary'>
         {(() => {
        switch (tasks.catogery) {
          case "Code":   return <CodeIcon />;
          case "Design": return <BrushIcon />;
          case "Explore":  return <FindInPageIcon />;
        }
      })()}
          </IconButton>
          </Tooltip>
          </span>
          </Typography>
          <Typography variant="subtitle1"  color='textSecondary' className={classes.cat} >
            {tasks.shortDescription}
          </Typography>
          <hr color='#16548a'></hr>
          <Typography variant='caption'>Task ID: {tasks.id}</Typography>
        </CardContent>

        <div className={classes.controls}>
        <Button variant="contained" component={Link} to={`/task/${tasks.id}`} className={classes.button}>
          View Task
        </Button>

        </div>
      </div>
    </Card>
    ))}
    <ScrollTop {...props}>
        <Fab color="secondary" size="small" aria-label="scroll back to top">
          <KeyboardArrowUpIcon />
        </Fab>
      </ScrollTop>
    </div>

  );
}
