import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Hidden from '@material-ui/core/Hidden';
import Button from '@material-ui/core/Button';
import { Theme, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import AccountIcon from '@material-ui/icons/AccountCircle';
import { Link } from 'react-router-dom';
import MenuItem from '@material-ui/core/MenuItem';

import {
  applicationsMenuItems,
  tasksMenuItems,
  applicationsUnderwriterMenu,
  applicationsOfficersMenu,
} from '../constants/menus';
import { IS_DEV_MODE } from '../../../constants';
import { Dialogs, Logo } from '../../common';
import { NavDrawer, NavMenu, CompaniesMenu } from '../components';
import SearchContainer from '../../search/containers/Search';
import { NavMenuItem } from '../types';

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    width: '100%',
    zIndex: 1100,
    position: 'fixed',
  },
  toolbar: {
    [theme.breakpoints.up('sm')]: {
      padding: theme.spacing(0, 1),
    },
  },
  grow: {
    flexGrow: 1,
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
    [theme.breakpoints.down('sm')]: {
      display: 'none',
    },
  },
  drawer: {
    display: 'none',
    marginLeft: -12,
    // marginRight: 20,
    [theme.breakpoints.down('sm')]: {
      display: 'block',
    },
  },
  title: {
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  menu: {
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'row',
  },
  rightMenu: {
    paddingLeft: theme.spacing(1),
  },
  developmentMode: {
    fontSize: '1rem',
    textTransform: 'uppercase',
    borderWidth: theme.spacing(0.25),
    borderRadius: theme.spacing(0.25),
    borderStyle: 'solid',
    borderColor: theme.palette.error.main,
    margin: theme.spacing(0, 0.5),
    padding: theme.spacing(0.5),
  }
}));

type OwnProps = {
  activeCompany: string;
  companies: string[];
  userLoggedIn: boolean;
  showDialog: boolean;
  showUnderwritingLink: boolean;
  showRetrieveIdLink?: boolean;
  opened: boolean;
  showSimulatorLink?: boolean;
  showUsersLink: boolean;
  showApplicationsLink: boolean;
  showAdminLink: boolean;
  showReportsLink: boolean;
  newMenuLinks: NavMenuItem[];
  devMenu: NavMenuItem[];
  accountMenuItems: NavMenuItem[];
  toggleDrawer: () => void;
  confirmUser: () => void;
  handleDevMenu: (title?: string) => void;
  updateCompany: (company: string) => void;
};

const NavBar: React.FC<OwnProps> =
  ({
     opened, toggleDrawer, userLoggedIn, accountMenuItems, showSimulatorLink, showUnderwritingLink,
     companies, showRetrieveIdLink, activeCompany, updateCompany, showUsersLink, showAdminLink, showApplicationsLink, showReportsLink, confirmUser,
     newMenuLinks, showDialog, handleDevMenu, devMenu,
   }) => {

    const classes = useStyles();

    const underwriterMenu = showUnderwritingLink ? applicationsUnderwriterMenu : [];

    const officersMenu = showRetrieveIdLink ? applicationsOfficersMenu : [];

    const applicationsMenu = [...applicationsMenuItems, ...underwriterMenu, ...officersMenu];

    const confirmAccount = () => {
      confirmUser();
    };

    const devModeInfo = () => IS_DEV_MODE && (
      <div className={classes.developmentMode}>
        Development
      </div>
    );

    return (
      <div className={classes.root}>
        <Dialogs.Info
          title={"Confirmation"}
          opened={showDialog}
          onClose={confirmAccount}
          closeText="Confirmed"
        >
          In order to use ZukIT, you must first confirm your Checklist account.
          <br />
          <br />
          Visit:
          <a href="https://api.checklist.com/account/contacts" target="_new">
            Checklist.com
          </a>
          <br />
          <br />
          Once confirmed, you will need to re-login to ZukIT
        </Dialogs.Info>
        <AppBar position="static">
          <Toolbar className={classes.toolbar}>
            <NavDrawer
              handleDevMenu={handleDevMenu}
              devMenu={IS_DEV_MODE ? devMenu : []}
              accountMenuItems={accountMenuItems}
              newMenuLinks={newMenuLinks}
              showUsersLink={showUsersLink}
              showApplicationsLink={showApplicationsLink}
              showAdminLink={showAdminLink}
              updateCompany={updateCompany}
              opened={opened}
              toggleDrawer={toggleDrawer}
              companies={companies}
              activeCompany={activeCompany}
              showSimulatorLink={showSimulatorLink}
              applicationsMenu={applicationsMenu}
              showReportsLink={showReportsLink}
            />
            <IconButton
              className={classes.drawer}
              color="inherit"
              aria-label="Open drawer"
              onClick={toggleDrawer}
            >
              <MenuIcon />
            </IconButton>
            <Logo />
            <Hidden smDown={true}>
              <div>
                {showAdminLink && (
                  <Link to={"/admin"}>
                    <Button color="inherit" className={classes.developmentMode}>Admin</Button>
                  </Link>
                )}
              </div>
              <div className={classes.menu}>
                <NavMenu
                  title="Tasks"
                  handleDevMenu={handleDevMenu}
                  menuItems={tasksMenuItems}
                  subMenu={
                    IS_DEV_MODE
                      ? {
                          title: "DEV",
                          handleDevMenu,
                          menuItems: devMenu,
                          renderedButton: MenuItem
                        }
                      : undefined
                  }
                />
                {showApplicationsLink && (
                  <NavMenu
                    title="Applications"
                    menuItems={applicationsMenu}
                    path="/applications"
                    top={true}
                    subMenu={{
                      title: "New",
                      menuItems: newMenuLinks,
                      renderedButton: MenuItem,
                      path: "/applications/new"
                    }}
                  />
                )}

                {showUsersLink && (
                  <Link to={"/users"}>
                    <Button color="inherit">Users</Button>
                  </Link>
                )}
                {showSimulatorLink && (
                  <Link to={"/simulator"}>
                    <Button color="inherit">Simulator</Button>
                  </Link>
                )}
                {showReportsLink && (
                       <Link to={"/reports"}>
                       <Button color="inherit">Reports</Button>
                     </Link>
                )}
              </div>
              {devModeInfo()}
            </Hidden>
            <div className={classes.grow} />
            <Hidden mdUp={true}>{devModeInfo()}</Hidden>
            <SearchContainer />
            <div className={`${classes.menu} ${classes.rightMenu}`}>
              <Hidden smDown={true}>
                <CompaniesMenu
                  updateCompany={updateCompany}
                  companies={companies}
                  activeCompany={activeCompany}
                />
                {userLoggedIn && (
                  <NavMenu
                    title="Account"
                    menuItems={accountMenuItems}
                    icon={AccountIcon}
                  />
                )}
              </Hidden>
            </div>
          </Toolbar>
        </AppBar>
      </div>
    );
  };

export default NavBar;
