import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  formControl: {
    margin: theme.spacing(3),
  },
}));

export default function CheckboxesGroup() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    All: true,
    Code: false,
    Design: false,
    Explore: false
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const { All, Code, Design, Explore } = state;
  const error = [All, Code, Desgin, Explore].filter((v) => v).length !== 2;

  return (
    <div className={classes.root}>
      <FormControl component="fieldset" className={classes.formControl}>
        <FormLabel component="legend">Filter Tasks</FormLabel>
        <FormGroup>
          <FormControlLabel
            control={<Checkbox checked={All} onChange={handleChange} name="All" />}
            label="All"
          />
          <FormControlLabel
            control={<Checkbox checked={Code} onChange={handleChange} name="Code" />}
            label="Code"
          />
          <FormControlLabel
            control={<Checkbox checked={Design} onChange={handleChange} name="Design" />}
            label="Design"
          />
          <FormControlLabel
            control={<Checkbox checked={Explore} onChange={handleChange} name="Explore" />}
            label="Explore"
          />
        </FormGroup>

      </FormControl>
    </div>
  );
}
